import styles from './fullRecipe.module.css';
// import recipeData from "../recipes/Recipe-data.json"

export default function FullRecipe ({individualRecipeData, closeHandler}) {
    return (
        <div className={styles.container}>
            <div className={styles.infoBox}>
                <h3>{individualRecipeData[0].title}</h3>
                <p>{individualRecipeData[0].ingredients}</p>
                <p>{individualRecipeData[0].steps}</p>
                <p>{individualRecipeData[0].category}</p>
                <p>{individualRecipeData.date}</p>
                <button onClick={closeHandler}>x</button>
            </div>
        </div>
    )
}
