import Header from "../header/Header.js";
import Dexie from "dexie";
import React, { useState, useEffect } from "react";

export default function AddRecipe() {
  //Datenbank erstellen
  const db = new Dexie("RecipeDatabase");
  //Version und Tabelle definieren
  db.version(1).stores({
    recipes: "title, ingredients, steps, date"
  });
  //Öffnen und auf Fehler prüfen
  db.open().catch(error => {
    console.log("An error ocurred:" + error);
  });

  //useStates definieren
  const [addTitle, setAddTitle] = useState("");
  const [addIngredients, setAddIngredients] = useState("");
  const [addSteps, setAddSteps] = useState("");
  const [addDate, setAddDate] = useState("");
  const [recipes, setRecipes] = useState("");

  //Abschicken der eingegebenen Daten
  const getRecipeData = event => {
    event.preventDefault();
    if (
      addTitle !== "" &&
      addIngredients !== "" &&
      addSteps !== "" &&
      addDate !== ""
    ) {
      let add = {
        title: addTitle,
        ingredients: addIngredients,
        steps: addSteps,
        date: addDate
      };
      db.recipes.add(add).then(async () => {
        //Ruft alle Rezepte aus der Datenbank ab
        let allRecipes = await db.recipes.toArray();
        //Erstellt alle Rezepte
        setRecipes(allRecipes);
      });
    }
  };

  //Erhält alle Rezepte aus der Datenbank
  useEffect(() => {
    const getRecipes = async () => {
      let allRecipes = await db.recipes.toArray();
      setRecipes(allRecipes);
    };
    getRecipes();
  }, []);

  //Löschen
  const deleteRecipe = async id => {
    console.log(id);
    db.recipes.delete(id);
    let allRecipes = await db.recipes.toArray();
    setRecipes(allRecipes);
  };

  let submitData;

  if (recipes.length > 0) {
    submitData = (
      <div>
        {recipes.map(recipe => {
          return (
            <div key={recipe.title}>
              <h2>{recipe.title}</h2>
              <h2>{recipe.ingredients}</h2>
              <h2>{recipe.steps}</h2>
              <h2>{recipe.date}</h2>
              <button onClick={() => deleteRecipe(recipe.title)}>Delete</button>
            </div>
          );
        })}
      </div>
    );
  } else {
    submitData = (
      <div>
        <h2>no recipes.</h2>
      </div>
    );
  }

  return (
    <>
      <Header title="Add new Recipe" />

      <form onSubmit={getRecipeData}>
        <div>
          <label>Title</label>
          <input
            type="text"
            onChange={event => setAddTitle(event.target.value)}
          ></input>
        </div>
        <div>
          <label>Ingredients</label>
          <input onChange={event => setAddIngredients(event.target.value)} />
        </div>
        <div>
          <label>Steps</label>
          <input onChange={event => setAddSteps(event.target.value)} />
        </div>
        <div>
          <label>Date</label>
          <input
            type="text"
            onChange={event => setAddDate(event.target.value)}
          ></input>
        </div>

        <input type="submit" value="Submit" />
      </form>
      {submitData}
    </>
  );
}
