import Header from "../header/Header.js";
// import Dexie from "dexie";
import useLocalStorage from "react-use-localstorage";
import React, { useState, useEffect } from "react";

export default function AddRecipe() {
  const [localTodos, setLocalTodos] = useLocalStorage("todos", "[]");

  //Titel
  const [addTitle, setAddTitle] = useState("");

  //Zutaten
  const [ingredients, setIngredients] = useState([]);
  const [currentIngredients, setCurrentIngredients] = useState("");

  //Schritte
  const [addSteps, setAddSteps] = useState("");

  //Datum
  const [addDate, setAddDate] = useState("");

  //   const [recipes, setRecipes] = useState("");

  useEffect(() => {
    const savedTodos = JSON.parse(localTodos);
    console.log(savedTodos);
    if (savedTodos) {
      setTodos(savedTodos);
    }
  }, []);

  useEffect(() => {
    const json = JSON.stringify(ingredients);
    setLocalTodos(json);
  }, [ingredients]);

  return (
    <>
      <Header title="Add new Recipe" />
      <div>
        <form>
          <label>Title</label>
          <input
            onChange={event => setAddTitle(event.target.value)}
            value={addTitle}
          ></input>
          <br />
          <br />
          <label>Ingredients</label>
          <textarea
            onChange={event => setIngredients(event.target.value)}
            value={ingredients}
          ></textarea>
          <button>+</button>
          <br />
          <br />
          <label>Steps</label>
          <textarea
            onChange={event => setAddSteps(event.target.value)}
            value={addSteps}
          ></textarea>
          <button>+</button>
          <br />
          <br />
          <label>Date</label>
          <input
            onChange={event => setAddDate(event.target.value)}
            value={addDate}
          ></input>
          <br />
          <br />
          <button> submit recipe </button>
        </form>
      </div>
    </>
  );
}
